import React from 'react';
import { Button, ButtonGroup, Card, Col, Container, Row } from 'react-bootstrap';
import { Rating } from './Rating';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Product } from './products';

interface AppProps {
  products: Product[]
}

interface AppState {
  filter: (product: Product) => boolean
}

class App extends React.Component<AppProps, AppState> {
  constructor(props: AppProps) {
    super(props);
    this.functionFilter = this.functionFilter.bind(this);
    this.state = {
      filter: (product: Product) => true
    }
  }

  private functionFilter(func: string) {
    this.setState({
      filter: (product: Product) => product.function.has(func)
    });
  }

  private resetFunctionFilter() {
    this.setState({
      filter: (product: Product) => true
    });
  }

  render() {
    return (
      <div className="App">
        <Container>
          <Row>
            <Col>
              <ButtonGroup>
                <Button variant="outline-dark" onClick={e => this.resetFunctionFilter()}>All</Button>
                <Button variant="outline-dark" onClick={e => this.functionFilter('Dry')}>Dry</Button>
                <Button variant="outline-secondary" onClick={e => this.functionFilter('Oily')}>Oily</Button>
                <Button variant="outline-secondary">Combination</Button>
                <Button variant="outline-secondary">Normal</Button>
                <Button variant="outline-secondary">Brightening</Button>
                <Button variant="outline-secondary">Anti-aging</Button>
              </ButtonGroup>
            </Col>
          </Row>
          <Row>
            {this.props.products.filter(this.state.filter).map(product => 
            <Col> 
              <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src={product.productImageUrl} />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Subtitle>{product.brand} 
                    <div style={{ float: 'right'}}>{product.price}</div>
                  </Card.Subtitle>
                  <Card.Subtitle><Rating score={product.rating} />{product.review}</Card.Subtitle>
                  <Card.Text>
                    {product.description}
                  </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
                </Card.Body>
              </Card>
            </Col>
            )}
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
